#!/usr/bin/python
"""
Purpose: Anagrams
    An Anagram is a sequence of numbers that can
    be formed by rearranging the digits of a string.
    Given a string that consists of only digits,
    modify the first half of the string so that it is
    an anagram of the second half.
    Determine the min. number of operations needed to
    complete the task
    - replacing any digit in the string with any other digit
     (0-9) is one operation
"""


def getAnagram(s):
    s_length = len(s)
    diff_count = 0
    first_half = s[: s_length // 2]
    second_half = s[s_length // 2 :]
    if sorted(first_half) != sorted(second_half):
        for first_ech, second_ech in zip(first_half, second_half):
            if first_ech != second_ech:
                diff_count += 1
    return diff_count


if __name__ == "__main__":
    assert getAnagram("123122") == 1
    assert getAnagram("123456") == 3
    assert getAnagram("0003187671") == 4
    assert getAnagram("786678") == 0
    assert (
        getAnagram(
            "2133326615727117001540261141407327120031612260317493998987993857958996993904815918596499598790384428"
        )
        == 30
    )
    assert (
        getAnagram(
            ""
        )
        == 569
    )
